function limiter($campo, $option = {}){
    // {$min = '', $max = ''}

    if($option.$min !== ''){
        if($campo.length <= $option$min){
            return false;
        }
    }

    if($option.$max !== ''){
        if($campo.length >= $max){
            return false;
        }
    }

    return true;
}

export default async function ValidaForms($data = []){

    let error = [];

    if(!$data){
        error.push('Falha ao validar campos!');
    }

    const numberCamps = $data.length;


    for(var i = 0; i < numberCamps; i++){
        const {$campo, $nomeCampo, $rules} = $data[i];

        if($rules){
            if($rules.required === true)
            {
                if($rules.type === String){
                    const min = limiter($campo, {$min : $rules.min})

                    if(!min){
                        error.push(`O campo ${$nomeCampo} nÃ£o deve ter menos de ${min} letras.`)
                    }
                }
            }
        }


    }

    // [
    //     {$campo, $nomeCampo, $rules}
    // ]

    // $rules = {
    //     min: Number,
    //     max: Number,
    //     type: Boolean & String & Number & Email,
    //     required : Boolean // True or False
    // }


    // Retorno
    if(error.length === 0){
        return false;
    }else{
        return error;
    }

}
